import React, { Component } from 'react';
import './App.css'
import VenueListings from './VenueListing'
import Header from './Header'


class App extends Component {
  constructor(props){
    super(props)
    this.state = {
    venues: [],
    buttonInput: ''
    }
  }

  componentDidMount() {
    let url = `https://api.foursquare.com/v2/venues/search?client_id=ST23AEQHHZXZSAVCBLBO4KZQZVA0KXNULNFPAVHFKMJLZ0OY&client_secret=NN3W2M14CHEJ2BCF21ORXCWWA5VYMXAWQYXTWG5414LU2RX0&v=20180323&ll=40.7,-74&query=${this.state.buttonInput}`
    fetch(url)
    .then(res => res.json())
    .then(data => {
      this.setState({
        venues: data.response.venues
      })
      console.log(this.state.venues)
    })
  }

  handleSubmit(e) {
    e.preventDefault()
    this.setState(prevState => ({
      buttonInput: e.target.value
    }))
  }

  handleChange(e) {
    this.setState({
      buttonInput: e.target.value,
    });
  }

  render() {
    return (
      <div className="App">
      <Header
      handleSubmit={this.handleSubmit}
      handleChange={this.handleChange}
      buttonInput={this.state.buttonInput}/>
      <VenueListings venues={this.state.venues} />
      </div>
    );
  }
}

export default App;

