import React, { Component } from 'react';

class Form extends Component {
  state = {
    input: '',
    binput: this.props.buttonInput
  }

  handleChange(e) {
    e.preventDefault()
    let input = e.target.value
    this.setState(prevState => ({
      input
    }))
    console.log(input)
  }

  handleSubmit(e){
    e.preventDefault()
    this.setState(prevState => ({
      binput: [...prevState, binput]
    }))
    console.log('inside Submit: ', binput)
  }

  render() {
    return(
      <form
      className="input"
      onSubmit={(e) => this.handleSubmit(e)}>
      <input
      placeholder="Whatcha Thinkin?"
      onChange= {(e) => this.handleChange(e)}
       />
      <button>Click Here</button>
      </form>
      )
  }
}

export default Form
